property.code:
const urlMapping = JSON.parse(document.getElementById('jiraConfig').dataset.authDisplayerUrls)
/**
 * Load or reload the displayer for the authenticator configuration
 * @param {*} element the parent container for the displayer content
 */
async function updateDisplayer(element) {
    const id = element.parentElement.getElementsBySelector(".jira-server-id > input")[0].value
    const authType = element.parentElement.getElementsBySelector("select.jira-config-authentication-type")[0].value
    const url = urlMapping[authType]
    let body = ''
    if (url !== undefined) {
        try {
            const response = await fetch(`${url}?id=${id}&authType=${authType}`);
            if (!response.ok) {
                console.error("can't get displayer content")
                return
            }
            body = await response.text()
        } catch (e) {
            console.error(e);
            return
        }
    }
    element.innerHTML = body
}

// First load of the authenticator displayer
for (const element of document.getElementsByClassName('jira-auth-configuration')) {
    new Promise(() => updateDisplayer(element))
}

// Reload the authenticator displayer if we change the authenticator
for (const element of document.getElementsByClassName('jira-config-authentication-type')) {
    element.addEventListener("change", async (event) => {
        const e = element.parentElement.parentElement.getElementsByClassName('jira-auth-configuration')[0]
        await updateDisplayer(e)
    })
}

// Avoid that the on the reload of the page we cancel the async save for the authentication configuration
// The save of the main configuration will be done after we saved all authenticator configuration
document.querySelector('#jiraConfig input.button').addEventListener('click', (e) => {
    e.preventDefault()
});

// Save the authenticator configuration
document.querySelector('#jiraConfig input.button').addEventListener('click', async (e) => {
    const form = document.getElementById('jiraConfig')
    const formData = new FormData(form);

    for (const url of Object.values(urlMapping)) {
        const fetchUrl = `${url}?save=true`
        try {
            const response = await fetch(fetchUrl, {
                method: "POST",
                body: formData,
            });
            if (!response.ok) {
                console.error("Can't update JIRA authenticator configuration")
            }
        } catch (e) {
            console.error(e);
        }
    }
    // run the default action as soon as the other query was sent
    form.submit();
});

// Update the ID fields of the authenticator configuration, so we have a consistent server ID matching between the JIRAServer configuration and the authenticator configuration
document.querySelectorAll('form#jiraConfig .jira-server-id input').forEach((element) => {
    element.addEventListener('change', (e) => {
        const idHiddenFiled = element.parentElement.parentElement.querySelector('td.jira-auth-configuration input[type="hidden"')
        if (idHiddenFiled != null) {
            idHiddenFiled.value = element.value
        }
    })
})
